import React, { Component } from "react";

class Form extends Component {
  handleSubmit = e => {
    e.preventDefault();
    this.props.displayResults(e.target[0].value);
  };
  render() {
    return (
      <div>
        <form onSubmit={this.handleSubmit}>
          <input type="text" />
          <input type="submit" />
        </form>
      </div>
    );
  }
}
export default Form;
