    }
  };
  render() {
    return (
      <div>
        <h1>{this.props.question.text}</h1>
        <button
          ref="trueBtn"
          className={this.state.answer ? "buttonCorrect" : "buttonWrong"}
          onClick={this.handleClick}
        >
          True
        </button>
        <button
          ref="falseBtn"
          className={this.state.answer ? "buttonCorrect" : "buttonWrong"}
          onClick={this.handleClick}
        >
          False
        </button>
      </div>
    );
  }
}
export default Question;
